  }
}

function parseEmail(email) {
  const regex = /^([\w.+-]+)@([\w-]+)(\.[\w.-]+)+$/;
  const match = email.match(regex);
  if (!match) return null;
  const local = match[1];
  const domainParts = (match[2] + match[3]).split(".");
  const tld = domainParts.slice(1).join(".");
  const domain = domainParts[0];
  return { local, domain, tld };
}
