const ymdhmsFormatter = (yyyy, mm, dd, hh, mi, sc) =>
  `${yyyy}-${mm}-${dd} ${hh}:${mi}:${sc}`;
const wdmyFormatter = (yyyy, mm, dd, hh, mi, sc, wd) =>
  `${wd}, ${dd}.${mm}.${yyyy}`;

const WD = ["So.", "Mo.", "Di.", "Mi.", "Do.", "Fr.", "Sa."];

function dateFormat(d = new Date(), formatter = ymdhmsFormatter) {
  const yyyy = d.getFullYear().toString();
  const mm = (d.getMonth() + 1).toString().padStart(2, "0");
  const dd = d.getDate().toString().padStart(2, "0");
  const hh = d.getHours().toString().padStart(2, "0");
  const mi = d.getMinutes().toString().padStart(2, "0");
  const sc = d.getSeconds().toString().padStart(2, "0");
  const wd = d.getDay();
  return formatter(yyyy, mm, dd, hh, mi, sc, WD[wd]);
}

